int when_floor(const char*c) {
		int floor = 0;
		for (int i=0; i<strlen(c); ++i) {
			if (c[i] == '(') floor ++;
			if (c[i] == ')') floor --;
			if (floor == -1) {
				return i+1;
			}
		}
		return -1;
}

